import React, { Component } from "react";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import axios from "axios";

const quotesUrl =
  "https://gist.githubusercontent.com/AbhishekChd/ab9949b618fcbf58ac84f9c8e88d6688/raw/90916f0d09a295eb5b2eea6c29a648e8c60b0e6f/5769a491e4b01190df7a9a70.json";

export default class QuoteBox extends Component {

  state = {
    quotes: [],
    quote: ''
  };

  componentDidMount() {
    axios
      .get(quotesUrl)
      .then(res => {

      })
      .then(res => this.getRandomQuote())
      .then(console.log(this.state))
      .catch(err => {
          console.log(err);
      });
  }

  getRandomQuote = async () => {
    const randomPos = Math.floor(Math.random() * this.state.quotes.length);
    console.log(this.state.quotes[randomPos]);
    const quote = this.state.quotes[randomPos];
    this.setState({
        quote: quote
    });
  };

  render() {
    return (
      <div>
        <Paper elevation={4} id="quote-box">
          <Typography align="center" variant="h3" gutterBottom id="text">
            titulo
          </Typography>
          <Typography variant="subtitle1" gutterBottom id="author">
            subtitulo
          </Typography>
          <Button id="new-quote">new quote</Button>
          <Button id="tweet-quote">tweet quote</Button>
        </Paper>
      </div>
    );
  }
}
