import React, { Component } from "react";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import axios from "axios";

const quotesUrl =
  "https://gist.githubusercontent.com/AbhishekChd/ab9949b618fcbf58ac84f9c8e88d6688/raw/90916f0d09a295eb5b2eea6c29a648e8c60b0e6f/5769a491e4b01190df7a9a70.json";

export default class QuoteBox extends Component {
  state = {
    quote: ""
  };

  componentDidMount() {
    this.getRandomQuote();
  }

  getRandomQuote = async res => {
    axios
      .get(quotesUrl)
      .then(res => {
        const randomPos = Math.floor(Math.random() * res.data.length);
        const quote = res.data[randomPos];
        this.setState({
          quote: quote
        });
      })
      .then(() => {
        console.log(this.state);
      })
      .catch(err => {
        console.log(err);
      });
  };

  render() {

    return (
      <div>
        <Paper elevation={4}>
          <Typography align="center" component="h3" variant="h3" gutterBottom >
            {this.state.quote}
          </Typography>
          <Typography component="subtitle1" variant="subtitle1" gutterBottom>
            subtitulo
          </Typography>
          <Button >new quote</Button>
          <Button >tweet quote</Button>
        </Paper>
      </div>
    );
  }
}
